
import * as fs from 'fs';

export namespace File { //to keep export from index clean until proposal is complete https://github.com/leebyron/ecmascript-more-export-from

    export async function exists(path: string): Promise<boolean> {
        return new Promise<boolean>((resolve, reject) => {
            fs.stat(path, (error, stat) => {
                if (error) {
                    if (error.code != 'ENOENT') reject(error);
                    resolve(false);
                }
                else if (!stat.isFile()) resolve(false);
                else resolve(true);
            });
        });
    }

    export async function readAllText(path: string): Promise<string> {
        return new Promise<string>((resolve, reject) => {
            fs.readFile(path, 'utf8', (error, data) => {
                if (error) reject(error);
                else resolve(data);
            });
        });
    }

    export async function writeAllText(path: string, data: string): Promise<void> {
        return new Promise<void>((resolve, reject) => {
            fs.writeFile(path, 'utf8', (error) => {
                if (error) reject(error);
                else resolve();
            });
        });
    }
}